// Função helper para converter preço do formato brasileiro
function parsePreco(precoStr: string): number {
  if (!precoStr || precoStr === "R$ -") return 0;
  const cleaned = precoStr.replace("R$", "").replace(",", ".").trim();
  return parseFloat(cleaned) || 0;
}

