// Exportando products com a propriedade imagem gerada a partir do procod
export const products: Product[] = productsData.map((p) => ({
  ...p,
  // As imagens estão em public/fotos e foram nomeadas com o código do produto.
  // Em runtime (por exemplo Next.js) o caminho público será "/fotos/<procod>.png"
  imagem: `/fotos/${p.procod}.png`,
}));

// Função para calcular desconto por grupo
export function calcularDescontoGrupo(quantidadeGrupo: number): number {
  if (quantidadeGrupo >= 24) return 0.20;
  if (quantidadeGrupo >= 18) return 0.15;
  if (quantidadeGrupo >= 12) return 0.10;
  if (quantidadeGrupo >= 6) return 0.05;
  return 0;
}

export function getDescontoLabel(quantidadeGrupo: number): string {
  if (quantidadeGrupo >= 24) return "20%";
  if (quantidadeGrupo >= 18) return "15%";
  if (quantidadeGrupo >= 12) return "10%";
  if (quantidadeGrupo >= 6) return "5%";
  return "0%";
}
